import React, { useState, useEffect, useCallback } from 'react';

const InterviewPage = ({ onComplete }) => {
  const [transcript, setTranscript] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [aiText, setAiText] = useState("Hello! Let's begin the interview. Could you please tell me a bit about yourself?");
  const [interviewHistory, setInterviewHistory] = useState([]);
  const [questionCount, setQuestionCount] = useState(0);

  const recognition = new (window.SpeechRecognition || window.webkitSpeechRecognition)();
  recognition.continuous = false;
  recognition.interimResults = false;
  recognition.lang = 'en-US';

  const synth = window.speechSynthesis;

  const sendToAI = useCallback(async (text) => {
    const updatedHistory = [...interviewHistory, { role: 'user', text }];
    setInterviewHistory(updatedHistory);
    
    try {
      const response = await fetch('http://localhost:3001/api/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ chatHistory: updatedHistory, userText: text }),
      });
      const data = await response.json();
      setAiText(data.message);
      setQuestionCount(prevCount => prevCount + 1);
    } catch (error) {
      console.error('Error fetching AI response:', error);
      setAiText("I am sorry, there was an error. Could you please try again?");
    }
  }, [interviewHistory]);

  const startListening = () => {
    setIsListening(true);
    recognition.start();
  };

  useEffect(() => {
    if (aiText) {
      const utterance = new SpeechSynthesisUtterance(aiText);
      utterance.onend = () => {
        if (questionCount < 5) {
          startListening();
        } else {
          onComplete([...interviewHistory, { role: 'ai', text: aiText }]); 
        }
      };
      synth.speak(utterance);
      setInterviewHistory(prevHistory => [...prevHistory, { role: 'ai', text: aiText }]);
    }
  }, [aiText, synth, recognition, interviewHistory, questionCount, onComplete]);

  useEffect(() => {
    recognition.onresult = (event) => {
      const last = event.results.length - 1;
      const text = event.results[last][0].transcript;
      setTranscript(text);
      setIsListening(false);
      sendToAI(text);
    };

    recognition.onerror = (event) => {
      console.error('Speech recognition error:', event.error);
      setIsListening(false);
    };

    return () => {
      recognition.stop();
    };
  }, [sendToAI, recognition]);

  return (
    <div>
      <p>AI: {aiText}</p>
      <p>You: {transcript || "Waiting for your response..."}</p>
      
      <div>
        <button onClick={startListening} disabled={isListening}>
          {isListening ? "Listening..." : "Speak now"}
        </button>
      </div>
    </div>
  );
};

export default InterviewPage;
